import React, { useState } from 'react'

const ERROR_IMG_SRC =    // backup image link
  'data:image/svg+xml;base64,'

export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)  // we told react to eye on image.

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, ...rest } = props

  return didError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}  // bg gray krega backup image ka
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={src} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}


// error handle-> did error- false -> real image show hoga
//                           true-> </div> ke under backup image show hoga

// ye component basically ek backup plan hai . jab real  images link break then its show.
// error show se save krta hai.